<?php$autoloader = function () {    $files = array(        __DIR__ . '/../../../autoload.php',        __DIR__ . '/../vendor/autoload.php',    );    foreach ($files as $file) {        if (is_file($file)) {            require_once $file;            return true;        }    }    return false;};if (!$autoloader()) {    die(        'You need to set up the project dependencies using the following commands:' . PHP_EOL .        'curl -s http://getcomposer.org/installer | php' . PHP_EOL .        'php composer.phar install' . PHP_EOL    );}use Symfony\Component\Console\Application;$application = new Application('Suzie');//$application->add(new \KooijmanInc\Suzie\Console\Command\Status\Model());$application->add(new \KooijmanInc\Suzie\Console\Command\Make\Model());return $application;