<?phpnamespace KooijmanInc\Suzie\Console\Command\Make\Generator;use KooijmanInc\Suzie\Exception\DuplicateModel;use KooijmanInc\Suzie\Model\Connection\ConnectionFactory;class ClassBuilder{    /**     * @var ConnectionFactory     */    protected ConnectionFactory $connectionFactory;    /**     * @var string     */    protected string $modelPath;    /**     * @var string     */    protected string $className;    /**     * @var array     */    protected array $modelTypes;    /**     * @var string     */    protected string $classType;    /**     * @var string     */    protected string $tableName;    /**     * @var string     */    protected string $database;    protected function writeModel(string $namespace): bool    {        dump($this);        dump($namespace);        $startNameSpace = str_replace('/', "\\", str_starts_with($this->modelPath, 'src') ? str_replace('src', 'App', $this->modelPath) : $this->modelPath);        $fullNamespace = $startNameSpace ."\\".$namespace."\\".$this->className;        $path = $this->modelPath.'/'.$this->classType.'/'.$namespace.'/'.$this->className.'.php';        dump($fullNamespace);        dump($path);        if (!file_exists($path)) {            $this->buildClass($fullNamespace, $startNameSpace, $this->classType, $namespace, $this->className);        } else {            throw new DuplicateModel("Failed to create '{$this->className}' {$this->classType} model. The model exists!");        }exit();        return false;    }    private function buildClass(string $fullNamespace, string $startNamespace, string $classType, string $namespace, string $className)    {        dump($fullNamespace, $startNamespace, $classType, $namespace, $className);    }}