<?phpnamespace KooijmanInc\Suzie\Console\Command\Make\Generator;use KooijmanInc\Suzie\Model\Connection\ConnectionFactory;class ClassGenerator extends ClassBuilder{    public function __construct(ConnectionFactory $connectionFactory, string $modelPath)    {        $this->connectionFactory = $connectionFactory;        $this->modelPath = $modelPath;    }    /**     * @param string $className     * @return ClassGenerator     */    public function setClassName(string $className): ClassGenerator    {        $this->className = $className;        return $this;    }    /**     * @param array $modelTypes     * @return ClassGenerator     */    public function setModelTypes(array $modelTypes): ClassGenerator    {        $this->modelTypes = $modelTypes;        return $this;    }    /**     * @param string $classType     * @return ClassGenerator     */    public function setClassType(string $classType): ClassGenerator    {        $this->classType = $classType;        return $this;    }    /**     * @param string $tableName     * @return ClassGenerator     */    public function setTableName(string $tableName): ClassGenerator    {        $this->tableName = $tableName;        return $this;    }    /**     * @param string $database     * @return ClassGenerator     */    public function setDatabase(string $database): ClassGenerator    {        $this->database = $database;        return $this;    }    public function write(string $namespace)    {        return $this->writeModel($namespace);    }}