<?phpnamespace KooijmanInc\Suzie\FormBuilder;use KooijmanInc\Suzie\FormBuilder\FormCollector\FormCollector;use KooijmanInc\Suzie\SuzieInterface;class FormBuilderFactory{    public function create(SuzieInterface $suzie, string $formBuilderClassName, array $data = [], array $base = [], bool $raw = false)    {//dump($data, $base);        $formBuilder = new $formBuilderClassName($suzie);        $formBuilder->toBeSetInputs($data);        if ($raw === true) {            $formBuilder = $this->fillRaw($formBuilder, $data, $base);        }        $formBuilder->completeForm();        return $formBuilder;    }    protected function fillRaw(FormBuilderInterface &$formBuilder, array &$data, array &$base)    {        $formBuilder->formCollector = new FormCollector($base['id'] ?? null);        dump($data);        $formBuilder->setRaw($data, $base, $base['id'] ?? null);//        foreach ($data as $name => $value) {//            $formBuilder->formInput->setRaw($name, $value, (array_key_exists($name, $base) ? $base[$name] : null), $base['id'] ?? null);//        }        return $formBuilder;    }}