<?phpnamespace KooijmanInc\Suzie\FormBuilder\FormParts\Form;use KooijmanInc\Suzie\Exception\NotSupported;/** * Class AbstractForm * @property string $id */abstract class AbstractForm implements FormInterface{    /**     * @var string     */    protected string $id;    /**     * @var string     */    protected string $uuid;    /**     * @var string     */    protected string $action = '/';    /**     * @param string $id     */    public function __construct(string $id)    {        $this->uuid = uniqid(str_replace('\\', '-', get_class($this)) . '-', true);        $this->id = $id;    }    /**     * @return string     */    public function getAction(): string    {        return $this->action;    }    public function &__get(string $name)    {        dump("__get: ", $name);        $accessor = "get" . ucfirst($name);        if (method_exists($this, $accessor) && is_callable([$this, $accessor])) {            $value = $this->$accessor();            return $value;        }        throw new NotSupported("__get: property or method ".get_called_class()."::{$name} is not supported");    }}