<?phpnamespace KooijmanInc\Suzie\Object\FormObject;use KooijmanInc\Suzie\FormBuilder\FormParts\Input\InputInterface;use KooijmanInc\Suzie\SuzieInterface;class ObjectStorage implements ObjectInterface{    protected SuzieInterface $suzie;    /**     * @var array     */    protected array $formInput = [];    public function __construct(SuzieInterface $suzie = null)    {        $this->suzie = $suzie;    }    public function attach(InputInterface &$object, string $name = null): bool    {        return ($this->formInput[$name] = $object) ? true : false;    }}